			 ENTR_ITEM_CODE
            ,SOC_TYPE_CODE
            ,PROD_CD
            ,ENTR_ITEM_NM
            ,STATUS_CD
            ,MEMO
            ,CREATE_ID
            ,CREATE_DT
		)
		VALUES
		(
			 #{entrItemCode}
			,#{socTypeCode}
			,#{prodCd}
			,#{entrItemNm}
			,#{statusCd}
			,#{memo, jdbcType=VARCHAR}
			,#{createId, jdbcType=VARCHAR}
			,sysdate
		)

        UPDATE TB_GFN_ENTR_ITEM
		SET
            UPDATE_DT = sysdate
            ,MEMO = #{memo, jdbcType=VARCHAR}

                ,SOC_TYPE_CODE = #{socTypeCode}


                ,PROD_CD = #{prodCd}


                ,ENTR_ITEM_NM = #{entrItemNm}


                ,STATUS_CD = #{statusCd}


                ,UPDATE_ID = #{updateId}

		WHERE ENTR_ITEM_CODE = #{entrItemCode}

UPDATE TB_GFN_ENTR_ITEM
SET
    UPDATE_DT = sysdate
    ,UPDATE_ID = #{updateId, jdbcType=VARCHAR}
	,STATUS_CD = '0'
WHERE ENTR_ITEM_CODE = #{entrItemCode}

