mapper.insert:
        INSERT INTO TB_PROMO_CODE
		(
			 PROMO_CODE
            ,EMAIL
            ,CREATE_DT
            ,DUE_DT
            ,MBR_ID
            ,STATUS_CD
            ,CONTACT_NO
            ,GFN_ID
            ,NAME
		)
		VALUES
		(
			 #{promoCode}
			,#{email}
			, sysdate
			,to_date(#{dueDt}||'235959', 'yyyyMMddhh24miss')
			,#{mbrId, jdbcType=VARCHAR}
			,#{statusCd}
			,#{contactNo, jdbcType=VARCHAR}
			,#{gfnId}
			,#{name}
		)

mapper.select:
SELECT PROMO_CODE ,EMAIL ,to_char(CREATE_DT, 'yyyyMMdd') as CREATE_DT ,to_char(DUE_DT, 'yyyyMMdd') as DUE_DT ,MBR_ID ,STATUS_CD ,CONTACT_NO ,GFN_ID FROM TB_PROMO_CODE
EMAIL = #{email}
OR CONTACT_NO = #{contactNo}

mapper.update:
UPDATE TB_PROMO_CODE
DUE_DT=to_date(#{dueDt}||'235959', 'yyyyMMddhh24miss'), MBR_ID=#{mbrId}, STATUS_CD=#{statusCd},
WHERE PROMO_CODE = #{promoCode}

UPDATE TB_GFN_MASTER
UPDATE_DT= sysdate ,DUE_DT= to_date(#{dueDt}||'235959', 'yyyymmddhh24miss')
WHERE GFN_ID = #{gfnId}
