mapper.select:
SELECT M.GFN_ID , M.CTN , M.SUB_NO , M.PROD_CD , E.ENTR_ITEM_CODE , E.ENTR_ITEM_NM , A.ADD_ITEM_CODE , A.ADD_ITEM_NM , M.CTN_STUS_CODE , M.CREATE_DT , M.LAST_LOGIN_DT FROM TB_GFN_MASTER M LEFT OUTER JOIN TB_GFN_ADD_ITEM A ON M.ADD_ITEM_CODE = A.ADD_ITEM_CODE LEFT OUTER JOIN TB_GFN_ENTR_ITEM E ON M.PLAN_ITEM_CODE = E.ENTR_ITEM_CODE
AND E.ENTR_ITEM_CODE = #{entrItemCode}
AND M.ADD_ITEM_CODE = #{addItemCode}
AND M.CREATE_DT >= TRUNC(TO_DATE(#{createStartDt},'yyyyMMdd'))
AND M.CREATE_DT <= TRUNC(TO_DATE(#{createEndDt},'yyyyMMdd') + 1)
AND M.SUB_NO = #{subNo}
AND M.CTN = #{ctn}
ORDER BY M.CREATE_DT
