mapper.select:
		/* LoginMapper.getLoginInfo */

		SELECT 
				MBR_ID 				AS mbrId
				, USE_YN 			AS useYn
				, LOGIN_FAIL_CNT 	AS loginFailCnt
                , TO_CHAR(LOGIN_FAIL_DT,'YYYYMMddHH24mi') AS loginFailDt
		FROM TB_MBR_INFO A
		WHERE 1 = 1
		AND MBR_ID = #{mbrId}
		AND MBR_PW = #{mbrPw}

mapper.insert:
		/* LoginMapper.updateLoginFailCnt */

		UPDATE TB_MBR_INFO
		SET
			LOGIN_FAIL_CNT = #{loginFailCnt} + 1,
            LOGIN_FAIL_DT = TO_DATE(#{loginFailDt}, 'yyyy/mm/dd hh24:mi:ss')
		WHERE	MBR_ID = #{mbrId}

mapper.update:
/* LoginMapper.setUserLock */

UPDATE TB_MBR_INFO
SET
	USE_YN = 'N'
WHERE	MBR_ID = #{mbrId}

/* LoginMapper.setLoginFailCntInit */

UPDATE TB_MBR_INFO
SET
	LOGIN_FAIL_CNT = 0
	, LOGIN_END_DT = TO_DATE(#{loginEndDt}, 'yyyy/mm/dd hh24:mi:ss')
WHERE MBR_ID = #{mbrId}

   		/* LoginMapper.setUserUnLock */

		UPDATE TB_MBR_INFO
		SET
			LOGIN_FAIL_CNT = 0
           ,USE_YN = 'Y'
		WHERE MBR_ID = #{mbrId}

mapper.select:
	/* LoginMapper.getApplyDate */

 	SELECT pw_apply_dt 
 	FROM tb_mbr_info 

 	WHERE mbr_id = #{mbrId}

    	/* LoginMapper.getOldPwValidate */

		SELECT
			COUNT(mbr_id) AS cnt
		FROM tb_mbr_info
		WHERE mbr_id = #{mbrId}
        AND ( mbr_pw = #{mbrpw} OR mbr_pw_old1 = #{mbrPw} OR mbr_pw_old2 = #{mbrPw} )

mapper.update:
/* LoginMapper.updateLoginEndDt */

UPDATE tb_mbr_info
SET
	login_end_dt = TO_DATE(#{loginEndDt}, 'yyyy/mm/dd hh24:mi:ss')
WHERE mbr_id = #{mbrId}
