mapper.select:
/* MbrMapper.getMemberList */
SELECT mbr_id ,mbr_pw ,mbr_nm ,company ,department ,tel ,email ,role_cd ,(SELECT code_nm FROM tb_role_mst WHERE role_code = A.role_cd) AS code_nm ,TO_CHAR(login_end_dt, 'YYYY-MM-DD HH24:MI:SS') AS login_end_dt ,use_yn ,login_fail_cnt ,TO_CHAR(apply_dt, 'YYYY-MM-DD') AS apply_dt FROM tb_mbr_info A WHERE 1 = 1
AND mbr_id = #{mbrId}

		/* MbrMapper.getMemberValidate */

		SELECT
			COUNT(mbr_id) AS cnt
		FROM tb_mbr_info
		WHERE 0 = 0

        	AND mbr_id = #{mbrId}


	        AND tel = #{tel}

mapper.insert:
/* MbrMapper.insertMbr */

INSERT INTO tb_mbr_info
(
	mbr_id,
	use_yn,
	role_cd,
	mbr_pw,
	mbr_nm,
	tel,
	email,
	company,
	department
)
VALUES
(
	#{mbrId}
	,'Y'
	,#{roleCd}
	,#{mbrPw}
	,#{mbrNm}
	,#{tel}
	,#{email}
	,#{mbrCompany}
	,#{mbrDptmt}
)

mapper.update:
    /* MbrMapper.updateMemberInfo */

	UPDATE tb_mbr_info
	SET
		email = #{email}
		,use_yn = #{useYn}

		,login_fail_cnt = 0


		,role_cd = #{roleCd}


	    ,tel = #{tel}


	    ,pw_apply_dt = TO_DATE(#{pwApplyDt}, 'yyyy/mm/dd hh24:mi:ss')
	    ,mbr_pw = #{mbrPw}    


	    ,mbr_pw_old1 = #{mbrPwOld1}


	    ,mbr_pw_old2 = #{mbrPwOld2}

	WHERE mbr_id = #{mbrId}

/* MbrMapper.setMemberPwdInit */

UPDATE tb_mbr_info
SET
	use_yn = 'Y'
	,login_fail_cnt = 0
	,mbr_pw = #{newPw}
	,mbr_pw_old1 = #{mbrPw}
	,mbr_pw_old2 = #{mbrPwOld1}
	,pw_apply_dt = TO_DATE(#{pwApplyDt}, 'yyyy/mm/dd hh24:mi:ss')
WHERE mbr_id = #{mbrId}

mapper.delete:
/* MbrMapper.deleteMbr */

DELETE FROM tb_mbr_info
WHERE mbr_id = #{mbrId}
